mapper.select:
select * from board

	where num = #{num}


	where btype = #{bType}
	
		 and head = #{filterType} 
	
	
		and mkinds = #{filterType}
	
	
		 and ${searchType} like '%${searchContent}%'
	
	order by regdate desc


	limit #{startrow},#{limit}

select count(*) from board

	where btype = #{bType}
	
		 and head = #{filterType} 
	
	
		and mkinds = #{filterType}
	
	
		 and ${searchType} like '%${searchContent}%'
